mapper.select:
SELECT receivable_total_amount
FROM receivable
WHERE receivable_id = #{receivableId}

mapper.update:
UPDATE receivable
SET receivable_status = #{status}
WHERE receivable_id = #{receivableId}

mapper.select:
SELECT hq_order_total_amount
FROM order_hq
WHERE hq_order_id = #{hqOrderId}

SELECT EXISTS (
  SELECT 1
  FROM receivable
  WHERE hq_order_id = #{hqOrderId}
    AND receivable_type = 'P'
)

SELECT EXISTS (
  SELECT 1
  FROM receivable
  WHERE store_order_id = #{storeOrderId}
    AND receivable_type = 'R'
)

SELECT store_order_total_amount
FROM order_store
WHERE store_order_id = #{storeOrderId}

SELECT COUNT(*) FROM ( SELECT r.vendor_id, DATE_FORMAT(r.receivable_date, '%Y-%m') AS baseMonth FROM receivable r JOIN vendor v ON r.vendor_id = v.vendor_id LEFT JOIN receivable_transaction rt ON r.receivable_id = rt.receivable_id WHERE r.receivable_type = 'P' AND r.receivable_status != 'C'
AND DATE_FORMAT(r.receivable_date, '%Y-%m') = #{baseMonth}
AND v.vendor_name LIKE CONCAT('%', #{vendorName}, '%')
GROUP BY r.vendor_id, DATE_FORMAT(r.receivable_date, '%Y-%m')
HAVING CASE WHEN IFNULL(SUM( CASE WHEN rt.transaction_type = 'P' THEN rt.transaction_amount ELSE 0 END ), 0) = 0 THEN 'UNPAID' WHEN IFNULL(SUM( CASE WHEN rt.transaction_type = 'P' THEN rt.transaction_amount ELSE 0 END ), 0) >= SUM(r.receivable_total_amount) THEN 'PAID' ELSE 'PARTIAL' END = #{payStatus}
) t

SELECT IFNULL(SUM(t.remain), 0) AS totalUnpaidAmount, COUNT(*) AS payableCount FROM ( SELECT r.vendor_id, DATE_FORMAT(r.receivable_date, '%Y-%m') AS baseMonth, SUM(r.receivable_total_amount) - IFNULL(SUM( CASE WHEN rt.transaction_type = 'P' THEN rt.transaction_amount ELSE 0 END ), 0) AS remain FROM receivable r JOIN vendor v ON r.vendor_id = v.vendor_id LEFT JOIN receivable_transaction rt ON r.receivable_id = rt.receivable_id WHERE r.receivable_type = 'P' AND r.receivable_status != 'C'
AND DATE_FORMAT(r.receivable_date, '%Y-%m') = #{baseMonth}
AND v.vendor_name LIKE CONCAT('%', #{vendorName}, '%')
GROUP BY r.vendor_id, DATE_FORMAT(r.receivable_date, '%Y-%m')
HAVING remain > 0
AND CASE WHEN remain = SUM(r.receivable_total_amount) THEN 'UNPAID' WHEN remain = 0 THEN 'PAID' ELSE 'PARTIAL' END = #{payStatus}
) t
